Home_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilegrid_6': 'Home_mobilegrid_6',
        'mobilegridcell_7': 'Home_mobilegridcell_7',
        'mobileimage_13': 'Home_mobileimage_13',
        'mobilelabel_19': 'Home_mobilelabel_19',
        'mobilegridcell_8': 'Home_mobilegridcell_8',
        'mobileimage_14': 'Home_mobileimage_14',
        'mobilelabel_20': 'Home_mobilelabel_20',
        'mobilegridcell_9': 'Home_mobilegridcell_9',
        'mobileimage_15': 'Home_mobileimage_15',
        'mobilelabel_21': 'Home_mobilelabel_21',
        'mobilegridcell_10': 'Home_mobilegridcell_10',
        'mobileimage_16': 'Home_mobileimage_16',
        'mobilelabel_22': 'Home_mobilelabel_22',
        'mobilegridcell_11': 'Home_mobilegridcell_11',
        'mobileimage_17': 'Home_mobileimage_17',
        'mobilelabel_23': 'Home_mobilelabel_23',
        'mobilegridcell_12': 'Home_mobilegridcell_12',
        'mobileimage_18': 'Home_mobileimage_18',
        'mobilelabel_24': 'Home_mobilelabel_24',
        'mobilebutton_3': 'Home_mobilebutton_3',
        'mobilebutton_5': 'Home_mobilebutton_5',
        'mobilebutton_2': 'Home_mobilebutton_2',
        'mobilebutton_4': 'Home_mobilebutton_4'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'Home';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    Home_beforeshow = function() {
        Appery.CurrentScreen = "Home";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_14CB_onLoad = Home_onLoad = function() {
        screen_14CB_elementsExtraJS();

        // TODO fire device events only if necessary (with JS logic)
        Home_deviceEvents();
        Home_windowEvents();
        screen_14CB_elementsEvents();
    }

    // screen window events
    screen_14CB_windowEvents = Home_windowEvents = function() {

        $('#Home').bind('pageshow orientationchange', function() {
            adjustContentHeightWithPadding();
        });

    }

    // device events
    Home_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_14CB_elementsExtraJS = Home_elementsExtraJS = function() {
        // screen (Home) extra code

    }

    // screen elements handler
    screen_14CB_elementsEvents = Home_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

    }

    $("#Home").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        Home_beforeshow();
    });

    if (runBeforeShow) {
        Home_beforeshow();
    } else {
        Home_onLoad();
    }

}

$("#Home").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    Home_js();
});